import React, {Component} from 'react';
import '../styles/App.css';
import axios from 'axios';
import SideBar from '../components/global-layout/SideBar';
import GreyBar from '../components/global-layout/GreyBar';
import AboutBottomNav from '../components/about-components/AboutBottomNav';
import AboutMenu from '../components/about-components/AboutMenu';
import settings from '../settings.png';


const subTabContainer = {
    gridColumn: '1/-1',
    width: '100%',
    backgroundColor: 'white',
    height: '95.5px',
    borderBottom: 'solid 1px rgba(0,0,0,0.1)'
}

const tabTitle = {
    
  width: '136px',
  height: '39px',
  fontSize: '35px',
  fontWeight: 'normal',
  fontStyle: 'normal',
  fontStretch: 'normal',
  lineHeight: 1.2,
  letterSpacing: 'normal',
  textAlign: 'left',
  color: '#000000',
  position: 'absolute',
  left: '9.1%',
  top: '18%'
}
const tabterm = {
    position: 'relative',
    top: '20.5px',
    display: 'grid',
    width: '250px',
    height: '25px',
    left: '85.4%',
    bottom: '15px',
    gridTemplateColumns: '105px 45px',
    gridColumnGap: '15px',
    height: '21px',
    fontSize: '18px',
    fontWeight: 600,
    fontStyle: 'normal',
    fontStretch: 'normal',
    lineHeight: 1.28,
    letterSpacing: 'normal',
    textAlign: 'left',
    color: '#000000'
}

export const basicTitle = {
    position: 'relative',
    left: '27px',
    top: '27px',
  width: '254px',
  height: '28px',
  fontSize: '25px',
  fontWeight: 500,
  fontStyle: 'normal',
  fontStretch: 'normal',
  lineHeight: 1.2,
  letterSpacing: 'normal',
  textAlign: 'left',
  color: '#000000'
}
export const optionalTitle = {
    position: 'relative',
    left: '27px',
    top: '52px',
  width: '254px',
  height: '28px',
  fontSize: '25px',
  fontWeight: 500,
  fontStyle: 'normal',
  fontStretch: 'normal',
  lineHeight: 1.2,
  letterSpacing: 'normal',
  textAlign: 'left',
  color: '#000000',
  marginBottom: '85px'
}
export const chargesblock= {
    gridColumn: '1/-1',
    width: '100%',
    display: 'flex',
    position: 'relative',
    top: '60px',
    marginBottom: '55px',
}
export const chargesblockLast= {
    gridColumn: '1/-1',
    width: '100%',
    display: 'flex',
    position: 'relative',
    top: '60px',
    marginBottom: '55px',
    paddingBottom: '50px',
    borderBottom: 'solid 1px rgba(0,0,0,0.2)'
}

export const checkbox = {
   position: 'relative',
   left: '27px',

  width: '25px',
  height: '25px',
  objectFit: 'contain',
  opacity: 0.5,
  fontSize: '25px',
  fontWeight: 'normal',
  fontStyle: 'normal',
  fontStretch: 'normal',
  lineHeight: 1.2,
  letterSpacing: 'normal',
  textAlign: 'left',
  color: '#44943e'

}

export const chargeType = {
    
  width: '45%',
  height: '21px',
  fontSize: '18px',
  fontWeight: 'normal',
  fontStyle: 'normal',
  fontStretch: 'normal',
  lineHeight: 1.28,
  letterSpacing: 'normal',
  textAlign: 'left',
  color: '#000000',
  position: 'relative',
  left : '71px',
  top: '-12.5px'
}

export const chargeAmount = {
    
  width: '92px',
  height: '21px',
  fontSize: '18px',
  fontWeight: 'normal',
  fontStyle: 'normal',
  fontStretch: 'normal',
  lineHeight: 1.28,
  letterSpacing: 'normal',
  textAlign: 'right',
  color: '#000000',
